package com.abdurashidov.retrofit1.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.abdurashidov.retrofit1.databinding.RvItemBindingimport com.abdurashidov.retrofit1.models.Planclass RvAdapter(val list:ArrayList<Plan>, val rvClick: RvClick): RecyclerView.Adapter<RvAdapter.Vh>() {    inner class Vh(val rvItem: RvItemBinding): RecyclerView.ViewHolder(rvItem.root){        fun onBind(label: Plan){            rvItem.title.text=label.sarlavha            rvItem.summary.text=label.matn            rvItem.holat.text=label.holat            rvItem.date.text=label.oxirgi_muddat        }    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): Vh {        return Vh(RvItemBinding.inflate(LayoutInflater.from(parent.context), parent, false))    }    override fun onBindViewHolder(holder: Vh, position: Int) {        holder.onBind(list[position])    }    override fun getItemCount(): Int {        return list.size    }    interface RvClick{        fun onClick(label: Plan)    }}