package com.abdurashidov.retrofit1.retrofitimport com.abdurashidov.retrofit1.models.AddTodoRequestimport com.abdurashidov.retrofit1.models.Planimport okhttp3.Callimport retrofit2.http.Bodyimport retrofit2.http.GETimport retrofit2.http.POSTinterface RetrofitService {    @GET("plan")    fun getAllTodo():retrofit2.Call<ArrayList<Plan>>    @POST("plan/")    fun addTodo(@Body addTodoRequest: AddTodoRequest):retrofit2.Call<Plan>}