package com.abdurashidov.retrofit1.utilsimport android.util.Logimport androidx.lifecycle.Lifecycleimport androidx.lifecycle.LifecycleObserverimport androidx.lifecycle.OnLifecycleEventclass MyObservable : LifecycleObserver {    private val TAG = "MyObservable"    @OnLifecycleEvent(Lifecycle.Event.ON_RESUME)    fun loading(){        Log.d(TAG, "loading: onResume")    }    @OnLifecycleEvent(Lifecycle.Event.ON_DESTROY)    fun disconnect(){        Log.d(TAG, "disconnect: onDestroy")    }}